commits.content:
.title-containers{
    background-color: rgba(98, 98, 98, 0.8);
    padding-left: 3%;
    padding-right: 3%;
    padding-bottom: 3%;
    border-radius: 0px 40px 0px 0px;
}

.button-edit-delete{
    background-color: rgba(51, 68, 83, 0.411);
    color: #fff;
    border: none;
    border-radius: 5px;
    cursor: pointer;
}



/* Estilos para o modal */
.modal {
    display: none;
    /* Escondido por padrão */
    position: fixed;
    /* Ficar fixo na tela */
    z-index: 1;
    /* Colocar o modal sobre tudo */
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: rgba(0, 0, 0, 0.4);
    /* Fundo preto com opacidade */
}

.modal-title{
    text-align: center;
    color: #ffffff;
    font-family: "Orbitron";
    font-size: 1.6em;
}

.modal-content {
    background-color: #333;
    color: #fff;
    font-family: "Orbitron";
    margin: 15% auto;
    padding: 20px;
    border: 1px solid #888;
    width: 80%;
}

.form{
    display: flex;
    flex-direction: column;

}

.modal-footer{
    display: flex;
    flex-direction: row;
    justify-content: center;
    gap: 30px;
}

.input{
     width: 100%;
     font-size: 1.4em;
     font-family: "Orbitron";
}

/* Botão para fechar o modal */
.close {
    color: #aaa;
    float: right;
    font-size: 28px;
    font-weight: bold;
}

.close:hover,
.close:focus {
    color: black;
    text-decoration: none;
    cursor: pointer;
}

/* Estilos para os modais */
.modal {
    display: none;
    position: fixed;
    z-index: 1;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: rgb(0, 0, 0);
    background-color: rgba(0, 0, 0, 0.4);
}

.modal-content {
    background-color: #fefefe;
    margin: 15% auto;
    padding: 20px;
    border: 1px solid #888;
    width: 80%;
}

.close {
    color: #aaa;
    float: right;
    font-size: 28px;
    font-weight: bold;
}

.close:hover,
.close:focus {
    color: black;
    text-decoration: none;
    cursor: pointer;
}

.button-edit-delete {
    cursor: pointer;
    color: #007bff;
    text-decoration: none;
    margin: 0 5px;
}

.button-edit-delete i {
    pointer-events: none;
}
