@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400..900&display=swap');





h3 {
  color: white;
  font-family: "Orbitron";
}

h4 {
  color: white;
  font-family: "Orbitron";
}

.cards-container{
  display: flex;
}

.card {
  background-color: #6d6d6d;
  border-radius: 5px;
  padding: 20px;
  color: white;
  margin: 10px;
  text-align: center;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: 0.3s;
}

.card:hover {
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

.card i {
  font-size: 60px;
  margin-bottom: 10px;
  color: #fff;
}
.card .value {
  font-size: 30px;
  font-family: Impact, Haettenschweiler, 'Arial Narrow Bold', sans-serif;
}

.metric {
  font-size: 18px;
  margin-bottom: 10px;
}



.historico-container {
  background-color: rgba(98, 98, 98, 0.8);
  border-radius: 5px 90px 5px 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #fff;
  width: 100%; /* Use a largura máxima possível */
  max-width: 525px; /* Define um limite máximo para a largura */
  padding: 7%;
  box-sizing: border-box; /* Inclui padding e border na largura total */
  margin: 0 auto; /* Centraliza o container horizontalmente */
}

.informacoes-container {
  background-color: rgba(98, 98, 98, 0.8);
  border-radius: 5px 5px 5px 90px;
  display: flex;
  padding: 7%;
  flex-direction: column;
  color: #fff;
  margin-top: 2%;
}



.login-form {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 36vh;
  width: 30vw;
  font-size: 25px;
  font-family: "Orbitron";

}


.parts-3d {
  width: 50vw;
  height: 80vh;
}

.login-text-box {
  width: 100%;
}




.navbar a:hover {
  background-color: #dddddd8c;
}

/* Style the header */
header {
  width: 100%;
  text-align: center;
  font-size: 35px;
  color: white;
  border: 0;
  padding-top: 10px;
}
