@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400..900&display=swap');

html {
  font-family: Arial, Helvetica, sans-serif;
}

.body-container {
  background-image: url("../images/bg3.jpg");
  background-size: cover;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-left: 0;
  margin-top: 0px;

}

.page-title {
  text-align: center;
  color: #ffffff;
  font-family: "Orbitron";
  font-size: 1.6em;
}

.btn {
  background-color: #C0C4D2;
  border: none;
  color: #fff;
  font-size: 1.5em;
  padding: 10px 10px;
  text-align: center;
  text-decoration: none;
  cursor: pointer;
  border-radius: 0 5px 5px 0;
  /* Ajusta apenas os cantos à direita */
  width: 60%;
  position: flex;
  font-family: "Orbitron";
}

.btn-padrao {
  background-color: #C0C4D2;
  border: none;
  color: #fff;
  font-size: 1.5em;
  padding: 10px 10px;
  text-align: center;
  text-decoration: none;
  cursor: pointer;
  border-radius: 0 5px 5px 0;
  /* Ajusta apenas os cantos à direita */
  width: 100%;
  position: flex;
  font-family: "Orbitron";
}

h2 {
  color: white;
  font-family: "Orbitron";
  font-size: 2.5em;
}


h6 {
  color: white;
  font-family: "Orbitron";
  font-size: 0.4em;
}

.tabela {
  font-size: 1.2em;
  border-collapse: collapse;
  width: 100%;
  /* Faz a tabela ocupar todo o espaço horizontal do container */
}

.tabela th,
.tabela td {
  border: 1px solid #000;
  padding: 8px;
  text-align: center;
  color: #fff;
}

.tabela th {
  background-color: #f2f2f2;
  color: #000;
}

.tabela tr:nth-child(even) {
  background-color: #333;
}

.tabela tr:hover {
  background-color: #666;
}



.buttons {
  margin-top: 5vh;
  margin-bottom: 5vh;
  display: flex;
  flex-direction: column;
  gap: 30px;
}


.logo-container {
  display: flex;
  justify-content: center;
  margin: 2vh;
}

.precision-tech-container {
  display: flex;
  justify-content: center;
}

.content-container {
  display: flex;
  flex-direction: column;
  margin-left: 2%;
  width: 90%;
  padding-left: 3%;
  padding-right: 3%;


}

.buttons-container {
  display: flex;
  flex-direction: column;
  gap: 10px
}

.footer {
  color: white;
  font-family: Arial, Helvetica, sans-serif;
  width: 100%;
  text-align: center;
  padding: 10px;
  margin-top: 10%;
}

.main-container {
  display: flex;
  width: 70%;
  height: 100%;
  justify-content: space-around;

}

.body-container {
  background-image: url("../images/bg3.jpg");
  background-size: cover;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-left: 0;

}


.logoCalculoPreciso {
  width: 250px;
  align-items: center;
}


.footer-text {
  font-size: 14px;
}

.login-container {
  background-color: rgba(98, 98, 98, 0.8);
  border-radius: 5px 90px 5px 5px;
  display: flex;
  flex-direction: column;
  columns: 40%;
  color: #fff;
}

.layout-container {
  background-color: rgba(98, 98, 98, 0.8);
  border-radius: 5px 90px 5px 5px;
  display: flex;
  flex-direction: column;
  color: #fff;
  font-family: "Orbitron";
  height: 0vh;
  width: 35vw;
}

.layout-container1 {
  background-color: rgba(98, 98, 98, 0.8);
  border-radius: 5px 5px 5px 90px;
  display: flex;
  flex-direction: column;
  color: #fff;
  font-family: "Orbitron";
  height: 40vh;
  width: 25vw;
  align-items: center;
  margin-top: 20px;
}

.logoCalculoPreciso {
  width: 150px;
  align-items: center;
}


.spinning-parts {
  display: flex;
  margin: 5%;
  background-color: rgba(98, 98, 98, 0.8);
  border-radius: 5px 5px 5px 90px;
}

#login-form {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 80vh;
  width: 30vw;
  font-size: 25px;
  font-family: "Orbitron";

}

.buttons-container {
  display: flex;
  flex-direction: column;
  gap: 10px
}

.parts-3d {
  width: 50vw;
  height: 80vh;
}

.login-text-box {
  width: 100%;
}

.sub-titulos {
  font-size: 1.6em;
  margin-top: 1%;
}

.btn-sidebar {
  background-color: #C0C4D2;
  height: 100px;
  font-weight: 600;
}


.navbar {
  display: flex;
  justify-content: flex-end;
  /* Alinha os itens no final do contêiner */
  background-color: #626371dc;
  padding: 20px;
  font-family: Orbitron;
  border-top-right-radius: 40px;
  border-bottom-right-radius: 40px;
}

.navbar a {
  font-size: 16px;
  color: white;
  text-align: center;
  padding: 14px 16px;
  text-decoration: none;
}


.navbar a:hover {
  background-color: #dddddd8c;
}

/* Style the header */
header {



  width: 100%;
  text-align: center;
  font-size: 35px;
  color: white;
  border: 0;
}

.container {
  width: 100%;
  height: 100%;

}
