/* Responsiveness */
@media (max-width: 768px) {
    .btn,
    .btn-padrao {
      font-size: 1.2em;
    }
  
    h2 {
      font-size: 2em;
    }
  
    h6 {
      font-size: 0.5em;
    }
  
    .tabela {
      font-size: 1em;
    }
  
    .main-container,
    .layout-container,
    .layout-container1,
    #login-form {
      width: 100%;
    }
  
    .navbar a {
      font-size: 14px;
      padding: 10px 12px;
    }
  
    header {
      font-size: 30px;
    }
  
    .footer-text {
      font-size: 12px;
    }
  }
